import { Plus, EyeFill, Trash, CardText } from "react-bootstrap-icons";
import { Link } from "react-router-dom";

function Home({ decks }) {
  let deckRend = decks.map((deck, index) => (
    <div className="row m-1">
      {" "}
      <div className="card w-100" key={index}>
        <div className="card-body">
          <h5 className="card-title">{deck.name}</h5>
          <p className="card-text">{deck.description}</p>{" "}
          <div className="row">
            {" "}
            <Link
              to={`/decks/${deck.id}`}
              className="btn btn-primary ml-1
         "
            >
              <EyeFill />
              &nbsp; View
            </Link>
            <Link
              to={`/decks/${deck.id}/study`}
              className="btn btn-primary ml-1"
            >
              <CardText /> &nbsp; Study
            </Link>
            <Link className="btn btn-danger offset-5 offset-md-7 offset-lg-9">
              <Trash />
            </Link>
          </div>
        </div>
      </div>
    </div>
  ));
  return (
    <>
      <div className="row">
        {" "}
        <Link to="/decks/new" className="btn btn-secondary text-white ml-3">
          <Plus /> Create Deck
        </Link>
      </div>
      {deckRend}
    </>
  );
}

export default Home;
