import { Link, useHistory } from "react-router-dom";
import { HouseFill } from "react-bootstrap-icons";
import { useState, useEffect } from "react";
import { updateDeck, readDeck } from "../../utils/api";
function EditDeck({ url, decks, setUpdated }) {
  const [deck, setDeck] = useState({ name: "", description: "" });
  // const [name, setName] = useState("");
  // const [description, setDescription] = useState("");
  const history = useHistory();
  // const nameHandler = (event) => {
  //   setName(event.target.value);
  // };
  // const descriptionHandler = (event) => {
  //   setDescription(event.target.value);
  // };

  useEffect(() => {
    const abortController = new AbortController();

    async function getDeck() {
      try {
        let result = await readDeck(deckId, abortController.signal);
        setDeck(result);
      } catch (error) {
        throw error;
      }
    }
    getDeck();

    return () => abortController.abort();
  }, []);

  const handleChange = ({ target }) => {
    setDeck({
      ...deck,
      [target.name]: target.value,
    });
  };
  const submitHandler = (event) => {
    event.preventDefault();
    let deckId = decks.length + 1;
    setDeck({ name: deck.name, description: deck.description });
    const abortController = new AbortController();

    try {
      async function UpdateDeck() {
        let result = await updateDeck(deck, abortController.signal);

        setUpdated(result);
      }
      UpdateDeck();
    } catch (error) {
      throw error;
    }

    history.push(`/decks/${deckId}`);
  };
  console.log(deck);
  return (
    <>
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
          <li className="breadcrumb-item">
            <Link to={"/"}>
              <HouseFill />
              &nbsp;Home
            </Link>
          </li>

          <li className="breadcrumb-item active" aria-current="page">
            <Link to={url}>Edit Deck</Link>
          </li>
        </ol>
      </nav>
      <h1>Create Deck</h1>
      <form onSubmit={submitHandler}>
        <div className="form-group">
          <label htmlFor="exampleInputEmail1">Name</label>
          <input
            type="text"
            className="form-control"
            id="DeckName"
            placeholder="Deck name"
            name="name"
            value={deck.name}
            onChange={handleChange}
            aria-describedby="deckName"
          />
        </div>
        <div className="form-group">
          <label htmlFor="DeckDescription">Description</label>
          <textarea
            class="form-control"
            id="exampleFormControlTextarea1"
            rows="3"
            placeholder="Brief decription of the deck"
            name="description"
            value={deck.description}
            onChange={handleChange}
          ></textarea>
        </div>
        <Link to={"/"} type="button" className="btn btn-secondary">
          Cancel
        </Link>
        <button type="submit" className="btn btn-primary m-1">
          Submit
        </button>
      </form>
    </>
  );
}

export default EditDeck;
