import { Link, useHistory } from "react-router-dom";
import { HouseFill } from "react-bootstrap-icons";
import { useState } from "react";
import { createDeck } from "../../utils/api";
function Create({ url, decks }) {
  const [deck, setDeck] = useState({});
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const history = useHistory();
  const nameHandler = (event) => {
    setName(event.target.value);
  };
  const descriptionHandler = (event) => {
    setDescription(event.target.value);
  };
  const submitHandler = (event) => {
    event.preventDefault();
    let deckId = decks.length + 1;
    setDeck({ id: deckId, name: name, description: description });
    const abortController = new AbortController();
    try {
      async function CreateDeck() {
        let result = await createDeck(deck, abortController.signal);
      }
      CreateDeck();
    } catch (error) {
      throw error;
    }
    //history.push(`/decks/${deckId}`);
  };
  console.log(name, description);
  return (
    <>
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
          <li className="breadcrumb-item">
            <Link to={"/"}>
              <HouseFill />
              &nbsp;Home
            </Link>
          </li>

          <li className="breadcrumb-item active" aria-current="page">
            <Link to={url}>Create Deck</Link>
          </li>
        </ol>
      </nav>
      <h1>Create Deck</h1>
      <form onSubmit={submitHandler}>
        <div className="form-group">
          <label htmlFor="exampleInputEmail1">Name</label>
          <input
            type="text"
            className="form-control"
            id="DeckName"
            placeholder="Deck name"
            name="name"
            value={name}
            onChange={nameHandler}
            aria-describedby="deckName"
          />
        </div>
        <div className="form-group">
          <label htmlFor="DeckDescription">Description</label>
          <textarea
            class="form-control"
            id="exampleFormControlTextarea1"
            rows="3"
            placeholder="Brief decription of the deck"
            name="description"
            value={description}
            onChange={descriptionHandler}
          ></textarea>
        </div>
        <Link to={"/"} type="button" className="btn btn-secondary">
          Cancel
        </Link>
        <button type="submit" className="btn btn-primary m-1">
          Submit
        </button>
      </form>
    </>
  );
}

export default Create;
