import { useParams, Link, useRouteMatch, useHistory } from "react-router-dom";
import { HouseFill } from "react-bootstrap-icons";
import { readDeck, updateCard, readCard } from "../../utils/api";
import { useEffect, useState } from "react";
function EditCard({ setUpdated }) {
  const { deckId, cardId } = useParams();
  const [deck, setDeck] = useState([]);
  const [card, setCard] = useState({ front: "", back: "" });
  const { url } = useRouteMatch();
  const history = useHistory();

  const handleChange = ({ target }) => {
    setCard({
      ...card,
      [target.name]: target.value,
    });
  };

  useEffect(() => {
    const abortController = new AbortController();

    async function getDeck() {
      try {
        let result = await readDeck(deckId, abortController.signal);
        setDeck(result);
      } catch (error) {
        throw error;
      }
    }
    getDecK();

    return () => abortController2.abort();
  }, []);

  useEffect(() => {
    const abortController = new AbortController();

    async function ReadCard() {
      try {
        let result = await readCard(cardId, abortController.signal);
        setcard(result);
      } catch (error) {
        throw error;
      }
    }
    getCard();

    return () => abortController.abort();
  }, []);

  console.log("addcard rendered");

  const submitHandler = (event) => {
    event.preventDefault();

    setCard((prev) => ({ ...prev, front: card.front, back: card.back }));
    const abortController = new AbortController();

    try {
      async function UpdateCard() {
        let result = await UpdateCard(card, abortController.signal);

        setUpdated(result);
      }
      UpdateCard();
    } catch (error) {
      throw error;
    }
    setCard({ front: "", back: "" });
  };

  return (
    <>
      {" "}
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
          <li className="breadcrumb-item">
            <Link to={"/"}>
              <HouseFill /> &nbsp;Home
            </Link>
          </li>
          <li className="breadcrumb-item">
            <Link to={`/decks/${deckId}`}>{deck.name}</Link>
          </li>
          <li className="breadcrumb-item active" aria-current="page">
            <Link to={url}>Add Card</Link>
          </li>
        </ol>
      </nav>
      <h2>Edit Card {card.id}</h2>
      <form onSubmit={submitHandler}>
        <div className="form-group">
          <label htmlFor="cardFront">Front</label>
          <textarea
            class="form-control"
            id="cardFront"
            rows="3"
            placeholder={card.front}
            name="front"
            value={card.front}
            onChange={handleChange}
          ></textarea>
        </div>
        <div className="form-group">
          <label htmlFor="DeckDescription">Back</label>
          <textarea
            class="form-control"
            id="exampleFormControlTextarea1"
            rows="3"
            placeholder={card.back}
            name="back"
            value={card.back}
            onChange={handleChange}
          ></textarea>
        </div>
        <Link
          to={`/decks/${deck.id}`}
          type="button"
          className="btn btn-secondary"
        >
          Cancel
        </Link>
        <button type="submit" className="btn btn-primary m-1">
          Save
        </button>
      </form>
    </>
  );
}

export default EditCard;
