import { Plus, EyeFill, Trash, CardText } from "react-bootstrap-icons";
import { Link, useRouteMatch } from "react-router-dom";

function Home({ decks }) {
  console.log(url, path);

  let deckRend = decks.map((deck, index) => (
    <div className="card col-md-6 offset-md-6 m-2" key={index}>
      <div className="card-body">
        <h5 className="card-title">{deck.name}</h5>
        <p className="card-text">{deck.description}</p>{" "}
        <Link className="btn btn-primary">
          <EyeFill />
          View
        </Link>
        <Link to={`decks/${deck.id}/study`} className="btn btn-primary">
          <CardText />
          Study
        </Link>
        <Link className="btn btn-primary">
          <Trash />
        </Link>
      </div>
    </div>
  ));
  return (
    <>
      {" "}
      <Link to="/decks/new" className="btn btn-secondary text-white">
        <Plus /> Create Deck
      </Link>
      <div className="row">{deckRend}</div>
    </>
  );
}

export default Home;
