import { Plus, EyeFill, Trash, CardText } from "react-bootstrap-icons";
import { Link, Route, Switch } from "react-router-dom";

function Home({ decks }) {
  let deckRend = decks.map((deck, index) => (
    <div className="row d-flex justify-content-center">
      <div className="card col-sm-8 col-md-10 col-lg-8 m-2" key={index}>
        <div className="card-body">
          <h5 className="card-title">{deck.name}</h5>
          <p className="card-text">{deck.description}</p>{" "}
          <div className="row">
            {" "}
            <Link className="btn btn-primary col-md-1">
              <EyeFill />
              View
            </Link>
            <Link
              to={`decks/${deck.id}/study`}
              className="btn btn-primary col-md-1"
            >
              <CardText />
              Study
            </Link>
            <Link className="btn btn-primary offset-9 col-md-1">
              <Trash />
            </Link>
          </div>
        </div>
      </div>
    </div>
  ));
  return (
    <div>
      {" "}
      <Link to="/decks/new" className="btn btn-secondary text-white">
        <Plus /> Create Deck
      </Link>
      {deckRend}
    </div>
  );
}

export default Home;
