import { useRouteMatch, Link, useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import { readDeck } from "../../utils/api";
function Study() {
  const [deck, setDeck] = useState({ cards: [] });
  const [iterator, setIterator] = useState(0);
  const [flipped, setFlipped] = useState(false);
  const { url } = useRouteMatch();
  const { deckId } = useParams();
  useEffect(() => {
    const abortContoller = new AbortController();
    async function LoadDeck() {
      try {
        let result = await readDeck(deckId);

        setDeck(result);
      } catch (error) {
        error.name === "AbortError" ? console.log("aborted") : console.error();
      }
    }
    LoadDeck();

    return () => abortContoller.abort();
  }, []);

  // console.log("cards = ", deck.cards);

  const clickHandler = () => {
    setFlipped((prev) => !prev);
  };

  const countHandler = () => {
    setIterator((prev) => prev + 1);
    setFlipped((prev) => !prev);
  };

  const cardsArray = deck.cards.map((card, index) => (
    <div className="card w-50">
      <div className="card-body">
        <h5 className="card-title">
          Card {index + 1} of {deck.cards.length}
        </h5>
        {!flipped ? (
          <p className="card-text">{card.front}</p>
        ) : (
          <p className="card-text">{card.back}</p>
        )}
        <button className="btn btn-primary" onClick={clickHandler}>
          Flip
        </button>
        {flipped ? (
          <button className="btn btn-primary m-1" onClick={countHandler}>
            Next
          </button>
        ) : (
          ""
        )}
      </div>
    </div>
  ));

  console.log("cards array is ", cardsArray);

  return (
    <>
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
          <li className="breadcrumb-item">
            <Link to={"/"}>Home</Link>
          </li>
          <li className="breadcrumb-item">
            <Link to={`/decks/${deckId}`}>Deck Name</Link>
          </li>
          <li className="breadcrumb-item active" aria-current="page">
            <Link to={url}>Study</Link>
          </li>
        </ol>
      </nav>
      {cardsArray[iterator]}
    </>
  );
}

export default Study;
