import { useParams, Link, useRouteMatch } from "react-router-dom";
import { useState, useEffect } from "react";
import { readDeck } from "../../utils/api";
import { CardText, Trash, Plus, HouseFill } from "react-bootstrap-icons";

function Deck({ decks }) {
  const { deckId } = useParams();
  const [deck, setDeck] = useState(decks.find((deck) => deckId === deck.id));
  const { url } = useRouteMatch();
  useEffect(() => {
    const abortController = new AbortController();

    async function getDeck() {
      try {
        let result = await readDeck(deckId, abortController.signal);
        setDeck(result);
      } catch (error) {
        throw error;
      }
    }
    getDeck();

    return () => abortController.abort();
  }, []);
  // const deck = decks.find((deck) => deck.id === deckId);
  // console.log(deck);
  // const cardsArray = deck.cards.map((card) => (
  //   <div className="card">
  //     <div className="card-body">
  //       <div className="d-flex">
  //         {" "}
  //         <p className="card-text m-2 ">Front: {card.front}</p>
  //         <p className="card-text m-2 ">Back: {card.back}</p>
  //       </div>
  //       <div className="d-flex justify-content-end">
  //         {" "}
  //         <Link className="btn btn-secondary m-1 ">Edit</Link>
  //         <Link className="btn btn-danger m-1">
  //           <Trash />
  //         </Link>
  //       </div>
  //     </div>
  //   </div>
  // ));

  return (
    <>
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
          <li className="breadcrumb-item">
            <Link to={"/"}>
              <HouseFill />
              &nbsp;Home
            </Link>
          </li>

          <li className="breadcrumb-item active" aria-current="page">
            <Link to={url}>{deck.name}</Link>
          </li>
        </ol>
      </nav>
      <div className="card w-100">
        <div className="card-body">
          <h5 className="card-title">{deck.name}</h5>
          <p className="card-text">{deck.description}</p>{" "}
          <div className="row">
            {" "}
            <Link
              to={`/decks/${deck.id}/edit`}
              className="btn btn-secondary ml-1
         "
            >
              Edit
            </Link>
            <Link
              to={`/decks/${deck.id}/study`}
              className="btn btn-primary ml-1"
            >
              <CardText /> &nbsp; Study
            </Link>
            <Link
              to={`/decks/${deck.id}/study`}
              className="btn btn-primary ml-1"
            >
              <Plus /> &nbsp; Add Cards
            </Link>
            <Link className="btn btn-danger offset-5 offset-md-7 offset-lg-8">
              <Trash />
            </Link>
          </div>
        </div>
      </div>
      {/* {deck.cards.length ? (
        <>
          <h2>Cards</h2>
          {cardsArray}
        </>
      ) : null} */}
    </>
  );
}

export default Deck;
