import { useParams, Link, useRouteMatch } from "react-router-dom";
import { HouseFill } from "react-bootstrap-icons";
import { readDeck } from "../../utils/api";
import { useEffect, useState } from "react";
function AddCard({ decks }) {
  const { deckId } = useParams();
  const [deck, setDeck] = useState([]);
  const [card, setCard] = useState({ front: "", back: "" });
  const { url } = useRouteMatch();

  const handleChange = ({ target }) => {
    setCard({
      ...card,
      [target.name]: target.value,
    });
  };

  useEffect(() => {
    const abortController = new AbortController();

    async function getDeck() {
      try {
        let result = await readDeck(deckId, abortController.signal);
        setDeck(result);
      } catch (error) {
        throw error;
      }
    }
    getDeck();

    return () => abortController.abort();
  }, []);

  console.log("addcard rendered");

  const submitHandler = (event) => {
    event.preventDefault();
    let deckId = decks.length + 1;
    setDeck({ name: deck.name, description: deck.description });
    const abortController = new AbortController();

    try {
      async function CreateDeck() {
        let result = await createDeck(deck, abortController.signal);

        setUpdated(result);
      }
      CreateDeck();
    } catch (error) {
      throw error;
    }

    history.push(`/decks/${deckId}`);
  };

  return (
    <>
      {" "}
      <nav aria-label="breadcrumb">
        <ol className="breadcrumb">
          <li className="breadcrumb-item">
            <Link to={"/"}>
              <HouseFill /> &nbsp;Home
            </Link>
          </li>
          <li className="breadcrumb-item">
            <Link to={`/decks/${deckId}`}>{deck.name}</Link>
          </li>
          <li className="breadcrumb-item active" aria-current="page">
            <Link to={url}>Study</Link>
          </li>
        </ol>
      </nav>
      <form onSubmit={submitHandler}>
        <div className="form-group">
          <label htmlFor="DeckDescription">Description</label>
          <textarea
            class="form-control"
            id="exampleFormControlTextarea1"
            rows="3"
            placeholder="Brief decription of the deck"
            name="description"
            value={card.front}
            onChange={handleChange}
          ></textarea>
        </div>
        <div className="form-group">
          <label htmlFor="DeckDescription">Description</label>
          <textarea
            class="form-control"
            id="exampleFormControlTextarea1"
            rows="3"
            placeholder="Brief decription of the deck"
            name="description"
            value={card.back}
            onChange={handleChange}
          ></textarea>
        </div>
        <Link to={"/"} type="button" className="btn btn-secondary">
          Cancel
        </Link>
        <button type="submit" className="btn btn-primary m-1">
          Submit
        </button>
      </form>
    </>
  );
}

export default AddCard;
